import nextConnect from "next-connect";
import middleware from "./database";
import { User, Calendar, Event } from "../../classes";

const handler = nextConnect();

handler.use(middleware);

handler.get(async req => {
    req.db.collection("userdata", function(err, collection) {
        collection.insertOne(
            new User("ken20001207", [
                new Calendar(
                    "預設行事曆",
                    ["#fd3721", "#b721ff"],
                    [
                        new Event("預設事項1", new Date(), new Date()),
                        new Event("預設事項2", new Date(), new Date()),
                        new Event("預設事項3", new Date(), new Date())
                    ]
                )
            ])
        );
    });
});

export default handler;
