import React from "react";
import Container from "@material-ui/core/container";
import Timeline from "../comps/timeline";

class index extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            events: [
                {
                    title: "網路班會",
                    cal: "待辦事項",
                    startTime: 600,
                    endTime: 660
                },
                {
                    title: "學習 React",
                    cal: "開發設計",
                    startTime: 675,
                    endTime: 705
                },
                {
                    title: "安裝 Ubuntu 作業系統",
                    cal: "開發設計",
                    startTime: 720,
                    endTime: 750
                },
                {
                    title: "建置桃園 V2ray 伺服器",
                    cal: "開發設計",
                    startTime: 840,
                    endTime: 885
                },
                {
                    title: "和家人吃午餐",
                    cal: "重要事項",
                    startTime: 900,
                    endTime: 945
                },
                {
                    title: "去星巴克找朋友",
                    cal: "待辦事項",
                    startTime: 1020,
                    endTime: 1035
                },
                {
                    title: "建置桃園 V2ray 伺服器",
                    cal: "開發設計",
                    startTime: 1140,
                    endTime: 1200
                }
            ]
        };
    }

    render() {
        const timeline = {
            overflowY: "scroll",
            maxHeight: "100vh",
            padding: 24
        };

        return (
            <Container maxWidth="sm" style={style}>
                <div style={timeline}>
                    <Timeline events={this.state.events} />
                </div>

                <style global jsx>{`
                    body {
                        background: #353535;
                    }
                    ::-webkit-scrollbar {
                        width: 5px;
                    }
                    ::-webkit-scrollbar-track {
                        -webkit-border-radius: 10px;
                        border-radius: 10px;
                        margin: 80px 0 5px 0;
                    }
                    ::-webkit-scrollbar-thumb {
                        -webkit-border-radius: 4px;
                        border-radius: 4px;
                        background: rgb(80, 80, 80);
                    }
                `}</style>
            </Container>
        );
    }
}

export default index;
