import nextConnect from "next-connect";
import middleware from "./database";
import { User, Calendar, Event } from "../../classes";

const handler = nextConnect();

handler.use(middleware);

handler.get(async (req, res) => {
    let doc = await req.db.collection("userdata", function(err, collection) {
        collection.insertOne(
            new User("ken20001207", [
                new Calendar(
                    "預設行事曆1",
                    ["#fd3721", "#b721ff"],
                    [
                        new Event("預設事項1", new Date("2020/2/23 10:00"), new Date("2020/2/23 11:00")),
                        new Event("預設事項2", new Date("2020/2/24 13:00"), new Date("2020/2/24 14:00")),
                        new Event("預設事項3", new Date("2020/2/23 16:00"), new Date("2020/2/23 17:00"))
                    ]
                ),
                new Calendar(
                    "預設行事曆2",
                    ["#B721FF", "#21D4FD"],
                    [
                        new Event("預設事項4", new Date("2020/2/24 10:00"), new Date("2020/2/24 11:00")),
                        new Event("預設事項5", new Date("2020/2/23 12:00"), new Date("2020/2/23 13:30")),
                        new Event("預設事項6", new Date("2020/2/24 15:00"), new Date("2020/2/24 15:30"))
                    ]
                )
            ])
        );
    });
    res.json({ code: 200 });
});

export default handler;
