import EventCard from "../comps/eventCard";
import Grid from "@material-ui/core/Grid";
import { Event } from "../classes";

function Timeline(props) {
    const todayEvents = props.events.filter(event => {
        if (event.startTime instanceof Date) {
            return (
                event.startTime.getFullYear() === 2020 &&
                event.startTime.getMonth() == 2 &&
                event.startTime.getDay() == 23
            );
        } else return false;
    });
    function fillEvents(events) {
        var time = new Date("2020/2/20 0:00:00");
        var filledEvents = [];
        events.map(event => {
            if (time < event.startTime) {
                filledEvents.push(new Event({ startTime: time, endTime: event.startTime }, true));
                filledEvents.push(event);
                time = event.endTime;
            } else {
                filledEvents.push(event);
                time = event.endTime;
            }
        });
        filledEvents.push(new Event("", null, time, new Date("2020/2/20 23:59:59")));
        return filledEvents;
    }

    return fillEvents(todayEvents).map(event => {
        if (event.cal !== null) {
            return (
                <Grid container spacing={1}>
                    <Grid item xs={2}>
                        <p style={{ color: "white", fontSize: 8 }}>
                            {event.startTime.getHours()}:
                            {event.startTime.getMinutes() < 10 ? "0" : "" + event.startTime.getMinutes()}
                        </p>
                    </Grid>
                    <Grid item xs={10}>
                        <EventCard event={event} />
                    </Grid>
                </Grid>
            );
        } else
            return (
                <Grid container spacing={1}>
                    <Grid item xs={2}></Grid>
                    <Grid item xs={10}>
                        <EventCard event={event} />
                    </Grid>
                </Grid>
            );
    });
}

export default Timeline;
