import EventCard from "../comps/eventCard";
import Grid from "@material-ui/core/Grid";
import React from "react";
import { Event } from "../classes";

function eventsbyDate(events, date) {
    return events.filter(event => {
        if (event instanceof Event)
            if (event.startTime instanceof Date) {
                return true;
            }
        return false;
    });
}

class Timeline extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            events: [
                {
                    startTime: "2020-02-22T16:00:00.922Z",
                    endTime: "2020-02-23T02:00:27.922Z",
                    duration: 600,
                    startTimeSrting: "0:0",
                    endTimeSting: "10:0",
                    durationString: "0:0 - 10:0",
                    isEmpty: true
                },
                {
                    startTime: "2020-02-23T02:00:27.922Z",
                    endTime: "2020-02-23T03:00:27.922Z",
                    duration: 60,
                    startTimeSrting: "10:0",
                    endTimeSting: "11:0",
                    durationString: "10:0 - 11:0",
                    isEmpty: false,
                    title: "預設事項1",
                    color: ["#fd3721", "#b721ff"]
                },
                {
                    startTime: "2020-02-23T03:00:27.922Z",
                    endTime: "2020-02-23T05:00:27.922Z",
                    duration: 120,
                    startTimeSrting: "11:0",
                    endTimeSting: "13:0",
                    durationString: "11:0 - 13:0",
                    isEmpty: true
                },
                {
                    startTime: "2020-02-23T05:00:27.922Z",
                    endTime: "2020-02-23T06:00:27.922Z",
                    duration: 60,
                    startTimeSrting: "13:0",
                    endTimeSting: "14:0",
                    durationString: "13:0 - 14:0",
                    isEmpty: false,
                    title: "預設事項2",
                    color: ["#fd3721", "#b721ff"]
                },
                {
                    startTime: "2020-02-23T06:00:27.922Z",
                    endTime: "2020-02-23T08:00:27.922Z",
                    duration: 120,
                    startTimeSrting: "14:0",
                    endTimeSting: "16:0",
                    durationString: "14:0 - 16:0",
                    isEmpty: true
                },
                {
                    startTime: "2020-02-23T08:00:27.922Z",
                    endTime: "2020-02-23T09:00:27.922Z",
                    duration: 60,
                    startTimeSrting: "16:0",
                    endTimeSting: "17:0",
                    durationString: "16:0 - 17:0",
                    isEmpty: false,
                    title: "預設事項3",
                    color: ["#fd3721", "#b721ff"]
                },
                {
                    startTime: "2020-02-23T09:00:27.922Z",
                    endTime: "2020-02-23T15:00:59.922Z",
                    duration: 360,
                    startTimeSrting: "17:0",
                    endTimeSting: "23:0",
                    durationString: "17:0 - 23:0",
                    isEmpty: true
                }
            ]
        };
    }
    render() {
        return this.state.events.map(event => {
            if (event instanceof Event) {
                console.log(event);
                if (!event.isEmpty) {
                    return (
                        <Grid container spacing={1} key={event.startTime}>
                            <Grid item xs={2}>
                                <p style={{ color: "white", fontSize: 8 }}>
                                    {event.startTime.getHours()}:
                                    {event.startTime.getMinutes() < 10 ? "0" : "" + event.startTime.getMinutes()}
                                </p>
                            </Grid>
                            <Grid item xs={10}>
                                <EventCard key={event.startTime} event={event} />
                            </Grid>
                        </Grid>
                    );
                } else {
                    return (
                        <Grid container spacing={1} key={event.startTime}>
                            <Grid item xs={2}></Grid>
                            <Grid item xs={10}>
                                <EventCard key={event.startTime} event={event} />
                            </Grid>
                        </Grid>
                    );
                }
            }
        });
    }
}

export default Timeline;
