import React from "react";
import Paper from "@material-ui/core/Paper";

function EventCard(props) {
    const event = props.event;

    var style = {
        height: (event.endTime - event.startTime) * 2,
        marginTop: 10,
        backgroundImage: "linear-gradient(160deg, #fc8e8e 0%, #fcd9c3 100%)",
        fontSize: 8,
        paddingLeft: 16,
        paddingTop: 8
    };

    if (event.cal == "重要事項")
        style.backgroundImage =
            "linear-gradient(315deg, #fd3721 0%, #b721ff 100%)";
    else if (event.cal == "開發設計")
        style.backgroundImage =
            "linear-gradient(315deg, #21D4FD 0%, #B721FF 100%)";
    else if (event.cal == "待辦事項")
        style.backgroundImage =
            "linear-gradient(123deg, #fdae21 0%, #fff521 100%)";
    else
        style = {
            height: event.endTime - event.startTime,
            marginTop: 10,
            backgroundColor: "transparent"
        };

    if (event.cal !== null)
        return (
            <Paper style={style}>
                <p style={{ color: "white" }}>{event.title}</p>
                <p style={{ color: "rgba(255,255,255,0.8)" }}>{event.cal}</p>
                <p style={{ color: "rgba(255,255,255,0.8)" }}>
                    {parseInt(event.startTime / 60)} :{event.startTime % 60} -{" "}
                    {parseInt(event.startTime / 60)} :{event.startTime % 60} ,
                    共 {event.endTime - event.startTime} 分鐘
                </p>
            </Paper>
        );
    else return <div style={style}></div>;
}

export default EventCard;
