import React from "react";
import Container from "@material-ui/core/container";
import Timeline from "../comps/timeline";

class index extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            events: [
                {
                    title: "網路班會",
                    cal: "待辦事項",
                    startTime: 600,
                    endTime: 660
                },
                {
                    title: "學習 React",
                    cal: "開發設計",
                    startTime: 675,
                    endTime: 705
                },
                {
                    title: "安裝 Ubuntu 作業系統",
                    cal: "開發設計",
                    startTime: 720,
                    endTime: 750
                },
                {
                    title: "建置桃園 V2ray 伺服器",
                    cal: "開發設計",
                    startTime: 840,
                    endTime: 885
                },
                {
                    title: "和家人吃午餐",
                    cal: "重要事項",
                    startTime: 900,
                    endTime: 945
                },
                {
                    title: "去星巴克找朋友",
                    cal: "提醒事項",
                    startTime: 1020,
                    endTime: 1035
                },
                {
                    title: "建置桃園 V2ray 伺服器",
                    cal: "開發設計",
                    startTime: 1140,
                    endTime: 1200
                }
            ]
        };
    }

    fillEmpty(events) {
        return new Promise(resolve => {
            var time = 0;
            var filled = [];
            events.map(event => {
                if (event.startTime > time) {
                    filled.push({
                        startTime: time,
                        endTime: event.startTime,
                        cal: null
                    });
                }
                filled.push(event);
            });
            resolve(filled);
        });
    }

    RenderEventList() {
        var time = 0;
        var renderEventList = [];
        this.state.events.forEach(event => {
            if (event.startTime > time) {
                renderEventList.push({
                    startTime: time,
                    endTime: event.startTime,
                    cal: null
                });
            }
            renderEventList.push(event);
        });
        return renderEventList;
    }

    render() {
        const style = {
            marginTop: 60,
            marginBottom: 60
        };

        return (
            <Container maxWidth="sm" style={style}>
                <Timeline events={this.RenderEventList} />

                <style global jsx>{`
                    body {
                        background: black;
                    }
                `}</style>
            </Container>
        );
    }
}

export default index;
