import React from "react";
import Paper from "@material-ui/core/Paper";

function EventCard(props) {
    const event = props.event;

    var style = {};

    if (event.cal == "重要事項")
        style = {
            height: 800,
            marginTop: 10,
            backgroundImage:
                "linear-gradient(160deg, #fc8e8e 0%, #fcd9c3 100%)",
            fontSize: 8,
            minHeight: 36,
            padding: 16
        };
    else if (event.cal == "開發設計")
        style = {
            height: event.endTime - event.startTime,
            marginTop: 10,
            backgroundImage:
                "linear-gradient(160deg, #8EC5FC 0%, #E0C3FC 100%)",
            fontSize: 8,
            minHeight: 36,
            padding: 16
        };
    else if (event.cal == "待辦事項")
        style = {
            height: event.endTime - event.startTime,
            marginTop: 10,
            backgroundImage:
                "linear-gradient(123deg, #fcc08e 0%, #fce5c3 100%)",
            fontSize: 8,
            minHeight: 36,
            padding: 16
        };
    else
        style = {
            height: event.endTime - event.startTime,
            marginTop: 10,
            backgroundColor: "transparent"
        };

    if (event.cal !== null)
        return (
            <Paper style={style}>
                <p style={{ color: "white" }}>{event.title}</p>
            </Paper>
        );
    else return <div style={style}></div>;
}

export default EventCard;
