import EventCard from "../comps/eventCard";
import Grid from "@material-ui/core/Grid";
import React from "react";
import { Event } from "../classes";

class Timeline extends React.Component {
    todayEvents(events, date) {
        events.filter(event => {
            if (event.startTime instanceof Date) {
                return (
                    event.startTime.getFullYear() === date.getFullYear() &&
                    event.startTime.getMonth() == date.getMonth() &&
                    event.startTime.getDate() == date.getDate()
                );
            } else return false;
        });
    }

    render() {
        console.log(this.props.events);
        return fillEvents(this.todayEvents(this.props.events, new Date())).map(event => {
            if (!event.isEmpty) {
                return (
                    <Grid container spacing={1} key={event.startTime}>
                        <Grid item xs={2}>
                            <p style={{ color: "white", fontSize: 8 }}>
                                {event.startTime.getHours()}:
                                {event.startTime.getMinutes() < 10 ? "0" : "" + event.startTime.getMinutes()}
                            </p>
                        </Grid>
                        <Grid item xs={10}>
                            <EventCard key={event.startTime} event={event} />
                        </Grid>
                    </Grid>
                );
            } else {
                return (
                    <Grid container spacing={1} key={event.startTime}>
                        <Grid item xs={2}></Grid>
                        <Grid item xs={10}>
                            <EventCard key={event.startTime} event={event} />
                        </Grid>
                    </Grid>
                );
            }
        });
    }
}

function fillEvents(events) {
    var time = new Date().setHours(0, 0, 0);
    var filledEvents = [];
    events.map(event => {
        if (time < event.startTime) {
            filledEvents.push(new Event({ startTime: time, endTime: event.startTime }, true));
            filledEvents.push(event);
            time = event.endTime;
        } else {
            filledEvents.push(event);
            time = event.endTime;
        }
    });
    filledEvents.push(new Event({ startTime: time, endTime: new Date("2020/2/23 23:59:59") }, true));
    return filledEvents;
}

export default Timeline;
