import EventCard from "../comps/eventCard";
import Grid from "@material-ui/core/Grid";

function fillByOneHour(filledEvents, startTime, time) {
    return new promises(function(resolve) {
        var n = parseInt((startTime - time) / 3600000);
        var count = 1;
        for (; n >= 0; count++, n--) {
            if (n > 0) {
                var h = time.getHours() + count - 1;
                filledEvents.push({
                    startTime: time.setHours(h),
                    endTime: time.setHours(h + 1),
                    cal: null
                });
            } else if (n == 0) {
                filledEvents.push({
                    startTime: time.setHours(time.getHours() + count - 1),
                    endTime: event.startTime,
                    cal: null
                });
            }
        }
        resolve();
    });
}

function Timeline(props) {
    function fillEvents(events) {
        var time = new Date("2020/2/20 0:00:00");
        var filledEvents = [];
        events.map(event => {
            if (time < event.startTime) {
                fillByOneHour(filledEvents, event.startTime, time).then(() => {
                    filledEvents.push(event);
                    time = event.endTime;
                });
            } else {
                filledEvents.push(event);
                time = event.endTime;
            }
        });
        return filledEvents;
    }

    return fillEvents(props.events).map(event => {
        if (event.cal !== null) {
            return (
                <Grid container spacing={1}>
                    <Grid item xs={2}>
                        <p style={{ color: "white", fontSize: 8 }}>
                            {event.startTime.getHours()} :{" "}
                            {(event.startTime.getMinutes() < 10 ? "0" : "") + event.startTime.getMinutes()}
                        </p>
                    </Grid>
                    <Grid item xs={10}>
                        <EventCard event={event} />
                    </Grid>
                </Grid>
            );
        } else
            return (
                <Grid container spacing={1}>
                    <Grid item xs={2}></Grid>
                    <Grid item xs={10}>
                        <EventCard event={event} />
                    </Grid>
                </Grid>
            );
    });
}

export default Timeline;
