import EventCard from "../comps/eventCard";
import Grid from "@material-ui/core/Grid";
import React from "react";
import { Event } from "../classes";

function eventsbyDate(events, date) {
    return events.filter(event => {
        if (event instanceof Event)
            if (event.startTime instanceof Date) {
                return true;
            }
        return false;
    });
}

class Timeline extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            todayEvents: [],
            filledEvents: []
        };
    }

    static getDerivedStateFromProps(nextProps) {
        return {
            todayEvents: nextProps.events,
            filledEvents: fillEvents(nextProps.events)
        };
    }

    render() {
        return this.state.filledEvents.map(event => {
            if (event instanceof Event) {
                console.log(event);
                if (!event.isEmpty) {
                    return (
                        <Grid container spacing={1} key={event.startTime}>
                            <Grid item xs={2}>
                                <p style={{ color: "white", fontSize: 8 }}>
                                    {event.startTime.getHours()}:
                                    {event.startTime.getMinutes() < 10 ? "0" : "" + event.startTime.getMinutes()}
                                </p>
                            </Grid>
                            <Grid item xs={10}>
                                <EventCard key={event.startTime} event={event} />
                            </Grid>
                        </Grid>
                    );
                } else {
                    return (
                        <Grid container spacing={1} key={event.startTime}>
                            <Grid item xs={2}></Grid>
                            <Grid item xs={10}>
                                <EventCard key={event.startTime} event={event} />
                            </Grid>
                        </Grid>
                    );
                }
            }
        });
    }
}

function startOfDay(date) {
    if (!date instanceof Date) {
        console.error("傳入的時間不是合法的 Date 物件。");
        return null;
    }
    date = new Date(date);
    var time = new Date();
    time.setTime(date.getTime());
    time.setHours(0, 0, 0);
    return time;
}

function endOfDay(date) {
    if (!date instanceof Date) {
        console.error("傳入的時間不是合法的 Date 物件。");
        return null;
    }
    date = new Date(date);
    var time = new Date();
    time.setTime(date.getTime());
    time.setHours(23, 59, 59);
    return time;
}

function fillEvents(events) {
    var filled = new Array();
    var time = startOfDay(events[0].startTime);
    events.map(event => {
        var startTime = new Date(event.startTime);
        var endTime = new Date(event.endTime);
        filled.push(new Event({ startTime: time, endTime: startTime }, true));
        filled.push(event);
        time.setTime(endTime.getTime());
    });
    filled.push(new Event({ startTime: time, endTime: endOfDay(events[0].startTime) }, true));
    return filled;
}

export default Timeline;
