export class Event {
    constructor(JSONObject, isEmpty) {
        this.startTime = new Date(JSONObject.startTime);
        this.startTime.setHours(this.startTime.getHours(), 0);
        this.endTime = new Date(JSONObject.endTime);
        this.endTime.setHours(this.endTime.getHours(), 0);
        this.duration = (this.endTime - this.startTime) / 60000;
        this.startTimeSrting = this.startTime.getHours() + ":" + this.startTime.getMinutes();
        this.endTimeSting = this.endTime.getHours() + ":" + this.endTime.getMinutes();
        this.durationString = this.startTimeSrting + " - " + this.endTimeSting;
        if (!isEmpty) {
            this.isEmpty = false;
            this.title = JSONObject.title;
            this.color = JSONObject.color;
        } else this.isEmpty = true;
    }

    isAllDayEvent() {
        return this.duration == 1440;
    }
}

export class Calendar {
    constructor(JSONObject) {
        this.title = JSONObject.title;
        this.color = JSONObject.color;
        this.events = JSONObject.events.map(event => {
            return new Event(event);
        });
    }
}

export class User {
    constructor(JSONObject) {
        this.username = JSONObject.username;
        this.calendars = JSONObject.calendars.map(calendar => {
            return new Calendar(calendar);
        });
    }
}
