import React from "react";
import Grid from "@material-ui/core/Grid";
import EventCard from "./eventCard";

import { Event } from "../classes";

class DayView extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        return this.props.events.map(event => {
            if (event instanceof Event) {
                return (
                    <Grid container spacing={1} key={event.title}>
                        <Grid item xs={2}>
                            <p style={{ color: "white", fontSize: 8 }}>{event.getStartTimeSrting()}</p>
                        </Grid>
                        <Grid item xs={10}>
                            <EventCard key={event.startTime} event={event} />
                        </Grid>
                    </Grid>
                );
            } else {
                console.error("渲染事件列表時接收到一個不符合規範的 event 物件。");
            }
        });
    }
}

export default DayView;
