import EventCard from "../comps/eventCard";
import Grid from "@material-ui/core/Grid";

function Timeline(props) {
    const style = {
        color: "white",
        fontSize: 8,
        lineHeight: 4
    };

    function fillEvents(events) {
        var time = Math.min(events[0].startTime, new Date("2020/2/20 6:00:00"));
        var filledEvents = [];
        events.map(event => {
            if (time < event.startTime) {
                var n = parseInt((event.startTime - time) / 3600000);
                var count = 0;
                while (n >= 0) {
                    if (n > 0)
                        filledEvents.push({
                            startTime: time.addHours(n - 1),
                            endTime: event.startTime,
                            cal: null
                        });
                    else if (n == 0)
                        filledEvents.push({
                            startTime: time,
                            endTime: event.startTime,
                            cal: null
                        });
                    count++;
                }
                filledEvents.push(event);
                time = event.endTime;
            } else {
                filledEvents.push(event);
                time = event.endTime;
            }
        });
        return filledEvents;
    }

    return fillEvents(props.events).map(event => {
        if (event.cal !== null) {
            return (
                <Grid container spacing={1}>
                    <Grid item xs={2}>
                        <p style={{ color: "white", fontSize: 8 }}>
                            {event.startTime.getHours()} :{" "}
                            {(event.startTime.getMinutes() < 10 ? "0" : "") +
                                event.startTime.getMinutes()}
                        </p>
                    </Grid>
                    <Grid item xs={10}>
                        <EventCard event={event} />
                    </Grid>
                </Grid>
            );
        } else
            return (
                <Grid container spacing={1}>
                    <Grid item xs={2}></Grid>
                    <Grid item xs={10}>
                        <EventCard event={event} />
                    </Grid>
                </Grid>
            );
    });
}

export default Timeline;
