import React from "react";
import Container from "@material-ui/core/container";
import EventCard from "../comps/eventCard";

/** Parse ics file string into event array */
function toEventArray(lines) {
    var events = [];
    var tempEvent = {};
    for (var line = 0; line < lines.length; line++) {
        if (lines[line].includes("BEGIN") && lines[line].includes("VEVENT")) {
            reading = true;
        } else if (lines[line].includes("END") && lines[line].includes("VEVENT")) {
            events.push(event);
            reading = false;
        }
        if (reading) {
            if (lines[line].includes("DTSTART")) {
                tempEvent.startTime = calenDate(lines[line].split(":")[1]);
            } else if (lines[line].includes("DTEND")) {
                tempEvent.endTime = calenDate(lines[line].split(":")[1]);
            } else if (lines[line].split(":")[0] == "SUMMARY") {
                tempEvent.title = lines[line].split(":")[1];
            }
        }
    }
}

/** Parse string of ics date format into date object */
function calenDate(icalStr) {
    var strYear = icalStr.substr(0, 4);
    var strMonth = parseInt(icalStr.substr(4, 2), 10) - 1;
    var strDay = icalStr.substr(6, 2);
    var strHour = icalStr.substr(9, 2);
    var strMin = icalStr.substr(11, 2);
    var strSec = icalStr.substr(13, 2);

    var oDate = new Date(strYear, strMonth, strDay, strHour, strMin, strSec);

    return oDate;
}

class icstool extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            events: []
        };
    }

    onChangeHandler = e => {
        const file = e.target.files[0];
        var reader = new FileReader();
        var events = [];
        reader.onload = function() {
            var lines = this.result.split("\n");
            var reading = false;
            var event = {};
            for (var line = 0; line < lines.length; line++) {
                if (lines[line].includes("BEGIN") && lines[line].includes("VEVENT")) {
                    reading = true;
                } else if (lines[line].includes("END") && lines[line].includes("VEVENT")) {
                    events.push(event);
                    reading = false;
                }
                if (reading) {
                    if (lines[line].includes("DTSTART")) {
                        event.startTime = calenDate(lines[line].split(":")[1]);
                    } else if (lines[line].includes("DTEND")) {
                        event.endTime = calenDate(lines[line].split(":")[1]);
                    } else if (lines[line].split(":")[0] == "SUMMARY") {
                        event.title = lines[line].split(":")[1];
                    }
                }
            }

            this.state.events = events;
        };
        reader.readAsText(file);
    };

    render() {
        var eventList = this.state.events.map(event => {
            return <EventCard event={event} />;
        });
        return (
            <Container maxWidth="lg">
                <input type="file" name="file" onChange={this.onChangeHandler} />
                <h1 style={{ color: "white" }}>解析結果</h1>
                {eventList}
                <style global jsx>{`
                    body {
                        background: #333333;
                    }
                    ::-webkit-scrollbar {
                        width: 5px;
                    }
                    ::-webkit-scrollbar-track {
                        -webkit-border-radius: 10px;
                        border-radius: 10px;
                        margin: 80px 0 5px 0;
                    }
                    ::-webkit-scrollbar-thumb {
                        -webkit-border-radius: 4px;
                        border-radius: 4px;
                        background: rgb(80, 80, 80);
                    }
                `}</style>
            </Container>
        );
    }
}

export default icstool;
