import React from "react";
import Paper from "@material-ui/core/Paper";

function EventCard(props) {
    const event = props.event;
    const duration = (event.endTime - event.startTime) / 60000;
    const startTime = new Date(event.startTime);
    const endTime = new Date(event.endTime);

    /** defalut styles */
    var cardStyle = {
        height: duration,
        marginTop: 10,
        backgroundImage: "linear-gradient(160deg, #fc8e8e 0%, #fcd9c3 100%)",
        fontSize: 8,
        paddingLeft: 16,
        paddingTop: 6,
        paddingBottom: 6
    };

    /** background color according to calendar */
    if (event.cal == "重要事項") cardStyle.backgroundImage = "linear-gradient(315deg, #fd3721 0%, #b721ff 100%)";
    else if (event.cal == "開發設計") cardStyle.backgroundImage = "linear-gradient(315deg, #21D4FD 0%, #B721FF 100%)";
    else if (event.cal == "待辦事項") cardStyle.backgroundImage = "linear-gradient(123deg, #fdae21 0%, #fff521 100%)";
    else
        cardStyle = {
            height: 60,
            marginTop: 10,
            backgroundColor: "transparent",
            borderBottomStyle: "solid",
            borderBottomColor: "#333333",
            borderBottomWidth: 1
        };

    /** compose event info of card */
    var lineAmount = parseInt(duration / 20) > 1 ? parseInt(duration / 20) - 1 : 1;
    var eventInfo = [];
    eventInfo.push(<p style={{ color: "white" }}>{event.title}</p>);
    eventInfo.push(
        <p style={{ color: "rgba(255,255,255,0.8)" }}>
            {startTime.getHours()}:{(startTime.getMinutes() < 10 ? "0" : "") + startTime.getMinutes()} -{" "}
            {endTime.getHours()}:{(endTime.getMinutes() < 10 ? "0" : "") + endTime.getMinutes()} , {duration} 分鐘
        </p>
    );
    eventInfo.push(<p style={{ color: "rgba(255,255,255,0.8)" }}>{event.cal}</p>);

    const emptyCard = <div style={cardStyle} />;

    // render
    if (event.cal !== null)
        return (
            <Paper style={cardStyle}>
                {eventInfo.slice(0, lineAmount).map(info => {
                    return info;
                })}
            </Paper>
        );
    else {
        var emptyCards = new Array(parseInt(duration / 60));
        return emptyCards.fill(emptyCard);
    }
}

export default EventCard;
