import React from "react";
import Paper from "@material-ui/core/Paper";

function emptyCard(startTime, endTime) {
    endTime = new Date(endTime);
    startTime = new Date(startTime);
    const duration = (endTime - startTime) / 60000;
    var cardStyle = {
        height: duration,
        backgroundColor: "transparent",
        borderBottomStyle: endTime.getMinutes() == 0 ? "solid" : "none",
        borderBottomColor: "#444444",
        borderBottomWidth: 1,
        borderTopStyle: startTime.getMinutes() == 0 ? "solid" : "none",
        borderTopColor: "#444444",
        borderTopWidth: 1
    };
    return <div style={cardStyle} />;
}

function EmptySections(start, end) {
    var arr = new Array(end.getHours() - start.getHours() + 1);
    if (start.getHours() == end.getHours()) {
        arr[0] = emptyCard(start, end);
    } else if (start.getHours() + 1 == end.getHours()) {
        arr[0] = emptyCard(start, new Date(end).setMinutes(0));
        arr[1] = emptyCard(new Date(end).setMinutes(0), end);
    } else {
        arr[0] = emptyCard(start, new Date(start).setHours(start.getHours() + 1, 0));
        for (var i = start.getHours() + 1, j = 1; i < end.getHours(); i++, j++) {
            arr[j] = emptyCard(new Date(start).setHours(i, 0), new Date(start).setHours(i + 1, 0));
        }
        arr[end.getHours() - start.getHours()] = emptyCard(new Date(end).setMinutes(0), end);
    }
    return arr;
}

function EventCard(props) {
    const event = props.event;
    const duration = (event.endTime - event.startTime) / 60000;
    const startTime = new Date(event.startTime);
    const endTime = new Date(event.endTime);

    this.state = {
        evelation: 0
    };

    function handleMouseOver(e) {
        this.setState({
            evelation: 3
        });
    }

    function handleMouseLeave(e) {
        this.setState({
            evelation: 0
        });
    }

    /** defalut styles */
    var cardStyle = {
        height: duration,
        backgroundImage: "linear-gradient(160deg, #fc8e8e 0%, #fcd9c3 100%)",
        fontSize: 8,
        paddingLeft: 16,
        paddingTop: 6,
        paddingBottom: 6
    };

    /** background color according to calendar */
    if (event.cal == "重要事項") cardStyle.backgroundImage = "linear-gradient(315deg, #fd3721 0%, #b721ff 100%)";
    else if (event.cal == "開發設計") cardStyle.backgroundImage = "linear-gradient(315deg, #21D4FD 0%, #B721FF 100%)";
    else if (event.cal == "待辦事項") cardStyle.backgroundImage = "linear-gradient(123deg, #fdae21 0%, #fff521 100%)";

    /** compose event info of card */
    var lineAmount = parseInt(duration / 20) > 1 ? parseInt(duration / 20) - 1 : 1;
    var eventInfo = [];
    eventInfo.push(
        <p style={{ color: "white" }}>
            {event.title}{" "}
            <strong style={{ marginLeft: 16, color: "rgba(255,255,255,0.4)" }}>
                {startTime.getHours()}:{(startTime.getMinutes() < 10 ? "0" : "") + startTime.getMinutes()} -{" "}
                {endTime.getHours()}:{(endTime.getMinutes() < 10 ? "0" : "") + endTime.getMinutes()}
            </strong>
        </p>
    );
    eventInfo.push(<p style={{ color: "rgba(255,255,255,0.8)" }}>{duration} 分鐘</p>);
    eventInfo.push(<p style={{ color: "rgba(255,255,255,0.8)" }}>{event.cal}</p>);

    // render
    if (event.cal !== null)
        return (
            <Paper style={cardStyle} evelation={this.state.evelation}>
                {eventInfo.slice(0, lineAmount).map(info => {
                    return info;
                })}
            </Paper>
        );
    else {
        return EmptySections(startTime, endTime).map(emptyCard => {
            return emptyCard;
        });
    }
}

export default EventCard;
