import React from "react";
import Paper from "@material-ui/core/Paper";

function EventCard(props) {
    const event = props.event;
    const duration = (event.endTime - event.startTime) / 60000;

    /** defalut styles */
    var cardStyle = {
        height: duration,
        marginTop: 10,
        backgroundImage: "linear-gradient(160deg, #fc8e8e 0%, #fcd9c3 100%)",
        fontSize: 8,
        paddingLeft: 16,
        paddingTop: 6,
        paddingBottom: 6
    };

    /** background color according to calendar */
    if (event.cal == "重要事項")
        cardStyle.backgroundImage =
            "linear-gradient(315deg, #fd3721 0%, #b721ff 100%)";
    else if (event.cal == "開發設計")
        cardStyle.backgroundImage =
            "linear-gradient(315deg, #21D4FD 0%, #B721FF 100%)";
    else if (event.cal == "待辦事項")
        cardStyle.backgroundImage =
            "linear-gradient(123deg, #fdae21 0%, #fff521 100%)";
    else
        cardStyle = {
            height: duration,
            marginTop: 10,
            backgroundColor: "transparent"
        };

    /** compose event info of card */
    var lineAmount = parseInt(duration / 15);
    var eventInfo = "";
    if (lineAmount >= 3) {
        eventInfo = (
            <div>
                <p style={{ color: "white" }}>{event.title}</p>
                <p style={{ color: "rgba(255,255,255,0.8)" }}>
                    {new Date(event.startTime).getTime()}
                    {event.endTime - event.startTime} 分鐘
                </p>
                <p style={{ color: "rgba(255,255,255,0.8)" }}>{event.cal}</p>
            </div>
        );
    } else if (lineAmount == 2) {
        eventInfo = (
            <div>
                <p style={{ color: "white" }}>{event.title}</p>
                <p style={{ color: "rgba(255,255,255,0.8)" }}>
                    {new Date(event.startTime).getUTCHours()}
                    {event.endTime - event.startTime} 分鐘
                </p>
            </div>
        );
    } else {
        eventInfo = (
            <div>
                <p style={{ color: "white" }}>{event.title}</p>
            </div>
        );
    }

    // render
    if (event.cal !== null) return <Paper style={cardStyle}>{eventInfo}</Paper>;
    else return <div style={cardStyle}></div>;
}

export default EventCard;
