import React from "react";
import Paper from "@material-ui/core/Paper";
import { Event } from "../classes";

class EventCard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            elevation: 0,
            style: {
                height: props.height != undefined ? props.height : this.duration,
                backgroundImage: "linear-gradient(160deg, #fc8e8e 0%, #fcd9c3 100%)",
                fontSize: 8,
                paddingLeft: 16,
                marginTop: props.height != undefined ? 15 : 0,
                paddingTop: 6,
                paddingBottom: 6
            }
        };
        this.handleMouseOver = this.handleMouseOver.bind(this);
        this.handleMouseLeave = this.handleMouseLeave.bind(this);
    }

    handleMouseOver() {
        this.setState({
            elevation: 20
        });
    }

    handleMouseLeave() {
        this.setState({
            elevation: 0
        });
    }

    render() {
        if (this.props.event instanceof Event) {
            console.log(this.props.event);
            if (this.props.event.isEmpty()) {
                return EmptySections(this.props.event.startTime, this.props.event.endTime).map(emptyCard => {
                    return emptyCard;
                });
            } else {
                /** background color */
                this.state.style.backgroundImage =
                    "linear-gradient(315deg, " + event.color[0] + " 0%, " + event.color[1] + " 100%)";

                /** compose event info of card */
                var lineAmount =
                    this.props.height != undefined
                        ? parseInt(this.props.height / 20) > 1
                            ? parseInt(this.props.height / 20) - 1
                            : 1
                        : parseInt(duration / 20) > 1
                        ? parseInt(duration / 20) - 1
                        : 1;
                var eventInfo = [];
                eventInfo.push(
                    event.isAllDayEvent() ? (
                        <p style={{ color: "white" }} key="title">
                            {event.title}{" "}
                            <strong style={{ marginLeft: 16, color: "rgba(255,255,255,0.4)" }}>全天事件</strong>
                        </p>
                    ) : (
                        <p style={{ color: "white" }}>
                            {event.title}{" "}
                            <strong style={{ marginLeft: 16, color: "rgba(255,255,255,0.4)" }}>
                                {startTime.getHours()}:
                                {(startTime.getMinutes() < 10 ? "0" : "") + startTime.getMinutes()} -{" "}
                                {endTime.getHours()}:{(endTime.getMinutes() < 10 ? "0" : "") + endTime.getMinutes()}
                            </strong>
                        </p>
                    )
                );
                if (!event.isAllDayEvent())
                    eventInfo.push(
                        <p style={{ color: "rgba(255,255,255,0.8)" }} key="duration">
                            {duration} 分鐘
                        </p>
                    );
                eventInfo.push(
                    <p style={{ color: "rgba(255,255,255,0.8)" }} key="cal">
                        {event.cal}
                    </p>
                );
                return (
                    <Paper
                        style={this.state.style}
                        elevation={this.state.elevation}
                        onMouseEnter={this.handleMouseOver}
                        onMouseLeave={this.handleMouseLeave}
                        onClick={this.handleClick}
                    >
                        {eventInfo.slice(0, lineAmount).map(info => {
                            return info;
                        })}
                    </Paper>
                );
            }
        } else {
            console.error("渲染事件卡片時接收到了不符合規範的 Event 物件。");
            return null;
        }
    }
}

function emptyCard(startTime, endTime) {
    endTime = endTime;
    startTime = startTime;
    const duration = (endTime - startTime) / 60000;
    var cardStyle = {
        height: duration,
        backgroundColor: "transparent",
        borderBottomStyle: endTime.getMinutes() == 0 ? "solid" : "none",
        borderBottomColor: "#444444",
        borderBottomWidth: 1,
        borderTopStyle: startTime.getMinutes() == 0 ? "solid" : "none",
        borderTopColor: "#444444",
        borderTopWidth: 1
    };
    return (
        <div style={cardStyle} key={startTime}>
            {startTime.getHours() +
                ":" +
                startTime.getMinutes() +
                " - " +
                endTime.getHours() +
                ":" +
                endTime.getMinutes()}
        </div>
    );
}

function EmptySections(start, end) {
    if (end.getHours() < start.getHours()) {
        end.setHours(23, 59, 59);
    }
    var arr = new Array(end.getHours() - start.getHours() + 1);
    if (start.getHours() == end.getHours()) {
        arr[0] = emptyCard(start, end);
    } else if (start.getHours() + 1 == end.getHours()) {
        var a = new Date(end);
        a.setMinutes(0);
        arr[0] = emptyCard(start, a);
        arr[1] = emptyCard(a, end);
    } else {
        var a = new Date(start);
        a.setHours(start.getHours() + 1, 0);
        arr[0] = emptyCard(start, a);
        for (var i = start.getHours() + 1, j = 1; i < end.getHours(); i++, j++) {
            var a = new Date(start);
            a.setHours(i, 0);
            var b = new Date(start);
            b.setHours(i + 1, 0);
            arr[j] = emptyCard(a, b);
        }
        var a = new Date(end);
        a.setMinutes(0);
        arr[end.getHours() - start.getHours()] = emptyCard(a, end);
    }
    return arr;
}

export default EventCard;
