import React from "react";
import Container from "@material-ui/core/container";
import DayView from "../comps/DayView";
import fetch from "isomorphic-unfetch";
import Grid from "@material-ui/core/Grid";
import { User, Calendar, Event } from "../classes";
import DayPicker from "react-day-picker";
import Dialog from "@material-ui/core/Dialog";
import Chip from "@material-ui/core/Chip";
import { TimePicker } from "antd";

function startOfDay(date) {
    if (!date instanceof Date) {
        console.error("傳入的時間不是合法的 Date 物件。");
        return null;
    }
    date = new Date(date);
    var time = new Date();
    time.setTime(date.getTime());
    time.setHours(0, 0, 0);
    return time;
}

function endOfDay(date) {
    if (!date instanceof Date) {
        console.error("傳入的時間不是合法的 Date 物件。");
        return null;
    }
    date = new Date(date);
    var time = new Date();
    time.setTime(date.getTime());
    time.setHours(23, 59, 59);
    return time;
}

function fillEvents(events, date) {
    var filled = new Array();
    var time = startOfDay(date);
    events.map(event => {
        var startTime = new Date(event.startTime);
        var endTime = new Date(event.endTime);
        if (startTime.getHours() > endTime.getHours()) {
            endTime.setHours(23, 59, 59);
        }
        filled.push(new Event({ startTime: time, endTime: startTime }, true));
        filled.push(event);
        time.setTime(endTime.getTime());
    });
    filled.push(new Event({ startTime: time, endTime: endOfDay(date) }, true));
    return filled;
}

function eventsToDispay(calendars, date) {
    var eventsToDispay = [];
    calendars.map(calendar => {
        calendar = new Calendar(calendar);
        calendar.events.map(event => {
            if (
                event.startTime.getFullYear() == date.getFullYear() &&
                event.startTime.getMonth() == date.getMonth() &&
                event.startTime.getDate() == date.getDate() &&
                !event.isAllDayEvent()
            ) {
                eventsToDispay.push(event);
            }
        });
    });
    eventsToDispay.sort((a, b) => a.startTime - b.startTime);
    return eventsToDispay;
}

class index extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedDay: new Date(),
            eventsToDispay: [],
            userdata: {},
            filled: [],
            selectedEvent: new Event({
                title: "選中的事件",
                startTime: new Date(),
                endTime: new Date(),
                color: ["#fd3721", "#b721ff"]
            })
        };
        this.handleDayClick = this.handleDayClick.bind(this);
    }

    async handleDayClick(day, { selected }) {
        this.setState({
            selectedDay: selected ? new Date() : day
        });
    }

    static async getInitialProps() {
        const res = await fetch("http://localhost:3000/api/getuserdata");
        const json = await res.json();
        var userdata = new User(json);
        var etd = eventsToDispay(userdata.calendars, new Date());
        var filled = fillEvents(eventsToDispay(userdata.calendars, new Date()), new Date());
        return { userdata: userdata, filled: filled, eventsToDispay: etd };
    }

    componentDidMount() {
        setTimeout(() => {
            var filled = fillEvents(this.props.eventsToDispay, new Date());
            this.setState({ filled: filled, userdata: this.props.userdata });
        }, 200);
        setTimeout(() => {
            fetch("http://localhost:3000/api/updateuserdata", { method: "post", body: JSON.stringify({ calendars: this.state.userdata.calendars }) });
        }, 1000);
    }

    render() {
        if (this.state.userdata.calendars != undefined) {
            var filled = fillEvents(eventsToDispay(this.state.userdata.calendars, this.state.selectedDay), this.state.selectedDay);

            return (
                <Container maxWidth="md">
                    <Grid container>
                        <Grid item xs={5}>
                            <div style={{ marginTop: 80 }}>
                                <DayPicker selectedDays={this.state.selectedDay} onDayClick={this.handleDayClick} />
                            </div>
                        </Grid>
                        <Grid item xs={7}>
                            <div
                                style={{
                                    overflowY: "scroll",
                                    maxHeight: "90vh",
                                    padding: 24
                                }}
                            >
                                <DayView events={filled} />
                            </div>
                        </Grid>
                    </Grid>

                    <Dialog maxWidth={"xs"} fullWidth={true} open={true}>
                        <Container maxWidth="lg" style={{ backgroundColor: "#333333", paddingTop: 30,paddingBottom: 30 }}>
                            <Chip
                                size="small"
                                label="重要事項"
                                style={{
                                    backgroundImage:
                                        "linear-gradient(315deg, " + this.state.selectedEvent.color[0] + " 0%, " + this.state.selectedEvent.color[1] + " 100%)"
                                }}
                            />
                            <h2 style={{ color: "white", marginTop: 10 }}>事件標題</h2>
                            <TimePicker value={this.state.value} />
                        </Container>
                    </Dialog>

                    <style global jsx>{`
                        body {
                            background: #333333;
                        }
                        ::-webkit-scrollbar {
                            width: 5px;
                        }
                        ::-webkit-scrollbar-track {
                            -webkit-border-radius: 10px;
                            border-radius: 10px;
                            margin: 80px 0 5px 0;
                        }
                        ::-webkit-scrollbar-thumb {
                            -webkit-border-radius: 4px;
                            border-radius: 4px;
                            background: rgb(80, 80, 80);
                        }
                        /* DayPicker styles */

                        .DayPicker {
                            display: inline-block;
                            font-size: 1rem;
                        }

                        .DayPicker-wrapper {
                            position: relative;

                            flex-direction: row;
                            padding-bottom: 1em;

                            -webkit-user-select: none;

                            -moz-user-select: none;

                            -ms-user-select: none;

                            user-select: none;
                        }

                        .DayPicker-Months {
                            display: flex;
                            flex-wrap: wrap;
                            justify-content: center;
                        }

                        .DayPicker-Month {
                            display: table;
                            margin: 0 1em;
                            margin-top: 1em;
                            border-spacing: 0;
                            border-collapse: collapse;

                            -webkit-user-select: none;

                            -moz-user-select: none;

                            -ms-user-select: none;

                            user-select: none;
                        }

                        .DayPicker-NavButton {
                            position: absolute;
                            top: 1em;
                            right: 1.5em;
                            left: auto;

                            display: inline-block;
                            margin-top: 2px;
                            width: 1.25em;
                            height: 1.25em;
                            background-position: center;
                            background-size: 50%;
                            background-repeat: no-repeat;
                            color: #8b9898;
                            cursor: pointer;
                        }

                        .DayPicker-NavButton:hover {
                            opacity: 0.8;
                        }

                        .DayPicker-NavButton--prev {
                            margin-right: 1.5em;
                            background-image: url("data:image/png;base64,");
                        }

                        .DayPicker-NavButton--next {
                            background-image: url("data:image/png;base64,");
                        }

                        .DayPicker-NavButton--interactionDisabled {
                            display: none;
                        }

                        .DayPicker-Caption {
                            display: table-caption;
                            margin-bottom: 0.5em;
                            padding: 0 0.5em;
                            text-align: left;
                            color: white;
                        }

                        .DayPicker-Caption > div {
                            font-weight: 500;
                            font-size: 1.15em;
                        }

                        .DayPicker-Weekdays {
                            display: table-header-group;
                            margin-top: 1em;
                        }

                        .DayPicker-WeekdaysRow {
                            display: table-row;
                        }

                        .DayPicker-Weekday {
                            display: table-cell;
                            padding: 0.5em;
                            color: #8b9898;
                            text-align: center;
                            font-size: 0.875em;
                        }

                        .DayPicker-Weekday abbr[title] {
                            border-bottom: none;
                            text-decoration: none;
                        }

                        .DayPicker-Body {
                            display: table-row-group;
                        }

                        .DayPicker-Week {
                            display: table-row;
                        }

                        .DayPicker-Day {
                            display: table-cell;
                            padding: 0.5em;
                            border-radius: 50%;
                            vertical-align: middle;
                            text-align: center;
                            cursor: pointer;
                            color: gray;
                        }

                        .DayPicker-WeekNumber {
                            display: table-cell;
                            padding: 0.5em;
                            min-width: 1em;
                            border-right: 1px solid #eaecec;
                            color: #8b9898;
                            vertical-align: middle;
                            text-align: right;
                            font-size: 0.75em;
                            cursor: pointer;
                        }

                        .DayPicker--interactionDisabled .DayPicker-Day {
                            cursor: default;
                        }

                        .DayPicker-Footer {
                            padding-top: 0.5em;
                        }

                        .DayPicker-TodayButton {
                            border: none;
                            background-color: transparent;
                            background-image: none;
                            box-shadow: none;
                            color: #4a90e2;
                            font-size: 0.875em;
                            cursor: pointer;
                        }

                        .DayPicker-Day--today {
                            color: white;
                            font-weight: 700;
                        }

                        .DayPicker-Day--outside {
                            color: #8b9898;
                            cursor: default;
                        }

                        .DayPicker-Day--disabled {
                            color: #dce0e0;
                            cursor: default;
                            /* background-color: #eff1f1; */
                        }

                        /* Example modifiers */

                        .DayPicker-Day--sunday {
                            background-color: #f7f8f8;
                        }

                        .DayPicker-Day--sunday:not(.DayPicker-Day--today) {
                            color: #dce0e0;
                        }

                        .DayPicker-Day--selected:not(.DayPicker-Day--disabled):not(.DayPicker-Day--outside) {
                            position: relative;

                            background-color: #4a90e2;
                            color: #f0f8ff;
                        }

                        .DayPicker-Day--selected:not(.DayPicker-Day--disabled):not(.DayPicker-Day--outside):hover {
                            background-color: #51a0fa;
                        }

                        .DayPicker:not(.DayPicker--interactionDisabled)
                            .DayPicker-Day:not(.DayPicker-Day--disabled):not(.DayPicker-Day--selected):not(.DayPicker-Day--outside):hover {
                            background-color: #f0f8ff;
                        }

                        /* DayPickerInput */

                        .DayPickerInput {
                            display: inline-block;
                        }

                        .DayPickerInput-OverlayWrapper {
                            position: relative;
                        }

                        .DayPickerInput-Overlay {
                            position: absolute;
                            left: 0;
                            z-index: 1;

                            background: white;
                            box-shadow: 0 2px 5px rgba(0, 0, 0, 0.15);
                        }
                    `}</style>
                </Container>
            );
        } else {
            return <p>loading</p>;
        }
    }
}

export default index;
