import React from "react";
import Container from "@material-ui/core/container";
import TextField from "@material-ui/core/TextField";
import EventCard from "../comps/eventCard";

class icstool extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            events: []
        };
    }

    onChangeHandler = e => {
        const file = e.target.files[0];
        var reader = new FileReader();
        var events = [];
        reader.onload = function() {
            var lines = this.result.split("\n");
            var reading = false;
            var event = {};
            for (var line = 0; line < lines.length; line++) {
                if (lines[line].includes("BEGIN") && lines[line].includes("VEVENT")) {
                    reading = true;
                } else if (lines[line].includes("END") && lines[line].includes("VEVENT")) {
                    events.push(event);
                    console.log(JSON.stringify(event));
                    reading = false;
                }
                if (reading) {
                    if (lines[line].includes("DTSTART")) {
                        console.log(lines[line].split(":")[1]);
                        event.startTime = new Date(lines[line].split(":")[1]);
                    } else if (lines[line].split(":")[0] == "DTEND") {
                        event.startTime = new Date(lines[line].split(":")[1]);
                    } else if (lines[line].split(":")[0] == "SUMMARY") {
                        event.title = new Date(lines[line].split(":")[1]);
                    }
                }
            }
        };
        this.state.events = events;
        reader.readAsText(file);
    };

    render() {
        var eventList = this.state.events.map(event => {
            return <EventCard event={event} />;
        });
        return (
            <Container maxWidth="lg">
                <input type="file" name="file" onChange={this.onChangeHandler} />
                <h1 style={{ color: "white" }}>解析結果</h1>
                {eventList}
                <style global jsx>{`
                    body {
                        background: #333333;
                    }
                    ::-webkit-scrollbar {
                        width: 5px;
                    }
                    ::-webkit-scrollbar-track {
                        -webkit-border-radius: 10px;
                        border-radius: 10px;
                        margin: 80px 0 5px 0;
                    }
                    ::-webkit-scrollbar-thumb {
                        -webkit-border-radius: 4px;
                        border-radius: 4px;
                        background: rgb(80, 80, 80);
                    }
                `}</style>
            </Container>
        );
    }
}

export default icstool;
