import EventCard from "../comps/eventCard";

function Timeline(props) {
    function getNextEvent(events, time) {
        return new Promise(resolve => {
            events.map(event => {
                if (event.startTime > time) {
                    resolve({
                        cal: null,
                        startTime: time,
                        endTime: event.startTime
                    });
                } else {
                    resolve(event);
                }
            });
        });
    }

    async function getFilledEvent() {
        var time = 0;
        var filledEvents = [
            {
                title: "網路班會",
                cal: "待辦事項",
                startTime: 600,
                endTime: 660
            },
            {
                title: "學習 React",
                cal: "開發設計",
                startTime: 675,
                endTime: 705
            },
            {
                title: "安裝 Ubuntu 作業系統",
                cal: "開發設計",
                startTime: 720,
                endTime: 750
            },
            {
                title: "建置桃園 V2ray 伺服器",
                cal: "開發設計",
                startTime: 840,
                endTime: 885
            },
            {
                title: "和家人吃午餐",
                cal: "重要事項",
                startTime: 900,
                endTime: 945
            },
            {
                title: "去星巴克找朋友",
                cal: "提醒事項",
                startTime: 1020,
                endTime: 1035
            },
            {
                title: "建置桃園 V2ray 伺服器",
                cal: "開發設計",
                startTime: 1140,
                endTime: 1200
            }
        ];
        while (time < 1440) {
            var nextEvent = await getNextEvent(filledEvents, time);
            filledEvents.push(nextEvent);
            time = nextEvent.endTime;
        }
        return filledEvents;
    }

    return getFilledEvent().map(event => <EventCard event={event} />);
}

export default Timeline;
