import EventCard from "../comps/eventCard";
import Grid from "@material-ui/core/Grid";

function SplitEmptySection(start, end) {
    return new Promise(resolve => {
        console.log(
            "開始分段 " + start.getHours() + ":" + start.getMinutes() + " 到 " + end.getHours() + ":" + end.getMinutes()
        );

        if (start.getHours() == end.getHours()) {
            resolve(
                new Array(1).fill({
                    startTime: start,
                    endTime: end,
                    cal: null
                })
            );
        } else if (start.getMinutes() + 1 == end.getMinutes()) {
            var arr = new Array(2);
            arr[0] = {
                startTime: start,
                endTime: new Date(start).setHours(start.getHours() + 1, 0),
                cal: null
            };
            arr[1] = {
                startTime: new Date(start).setHours(start.getHours() + 1, 0),
                endTime: end,
                cal: null
            };
            resolve(arr);
        } else {
            var arr = new Array(end.getHours() - start.getHours() + 1);
            arr[0] = {
                startTime: start,
                endTime: new Date(start).setHours(start.getHours() + 1, 0),
                cal: null
            };
            var pointer = 1;
            while (pointer < end.getHours() - start.getHours()) {
                arr[pointer++] = {
                    startTime: new Date(start).setHours(start.getHours() + pointer, 0),
                    endTime: new Date(start).setHours(start.getHours() + pointer + 1, 0),
                    cal: null
                };
            }
            arr[end.getHours() - start.getHours()] = {
                startTime: new Date(end).setMinutes(0),
                endTime: end,
                cal: null
            };
            resolve(arr);
        }
    });
}

function Timeline(props) {
    function fillEvents(events) {
        var time = new Date("2020/2/20 0:00:00");
        var filledEvents = [];
        events.map(async event => {
            new Promise(async resolve => {
                if (time < event.startTime) {
                    var splitedEmptySection = await SplitEmptySection(time, event.startTime);
                    Promise.resolve(
                        splitedEmptySection.map(event => {
                            console.log(
                                new Date(event.startTime).toLocaleString() +
                                    " 到 " +
                                    new Date(event.endTime).toLocaleString()
                            );
                            filledEvents.push(event);
                        })
                    );
                }
            }).then(() => {
                filledEvents.push(event);
                resolve(time.setTime(event.endTime));
            });
        });
        filledEvents.push({
            startTime: time,
            endTime: new Date("2020/2/20 24:00:00"),
            cal: null
        });
        return filledEvents;
    }

    return fillEvents(props.events).map(event => {
        if (event.cal !== null) {
            return (
                <Grid container spacing={1}>
                    <Grid item xs={2}>
                        <p style={{ color: "white", fontSize: 8 }}>
                            {event.startTime.getHours()}:
                            {(event.startTime.getMinutes() < 10 ? "0" : "") + event.startTime.getMinutes()}
                        </p>
                    </Grid>
                    <Grid item xs={10}>
                        <EventCard event={event} />
                    </Grid>
                </Grid>
            );
        } else
            return (
                <Grid container spacing={1}>
                    <Grid item xs={2}></Grid>
                    <Grid item xs={10}>
                        <EventCard event={event} />
                    </Grid>
                </Grid>
            );
    });
}

export default Timeline;
