import EventCard from "../comps/eventCard";
import Grid from "@material-ui/core/Grid";

async function addNewEvent(eventList, eventData) {
    return new Promise(resolve => {
        eventList.push({
            startTime: eventData.start,
            endTime: eventData.end,
            cal: eventData.calendar
        });
        resolve(eventData.end);
    });
}

async function fillPerOneHour(filledEvents, from, to) {
    console.log("await fillPerOneHour(filledEvents, " + from.getHours() + ", " + to.getHours() + ");");
    return new Promise(async resolve => {
        while (to.getHours() > Date(from).getHours()) {
            var temp = new Date(from).setHours(from.getHours() + 1, 0);
            from = await addNewEvent(filledEvents, {
                start: from,
                end: temp,
                calendar: null
            });
        }
    });
}

function Timeline(props) {
    function fillEvents(events) {
        var time = new Date("2020/2/20 0:00:00");
        var filledEvents = [];
        events.map(async event => {
            if (time < event.startTime) {
                await fillPerOneHour(filledEvents, time, event.startTime);
                filledEvents.push(event);
                time = event.endTime;
            } else {
                filledEvents.push(event);
                time = event.endTime;
            }
        });
        filledEvents.push({
            startTime: time,
            endTime: new Date("2020/2/20 24:00:00"),
            cal: null
        });
        return filledEvents;
    }

    return fillEvents(props.events).map(event => {
        if (event.cal !== null) {
            return (
                <Grid container spacing={1}>
                    <Grid item xs={2}>
                        <p style={{ color: "white", fontSize: 8 }}>
                            {event.startTime.getHours()}:
                            {(event.startTime.getMinutes() < 10 ? "0" : "") + event.startTime.getMinutes()}
                        </p>
                    </Grid>
                    <Grid item xs={10}>
                        <EventCard event={event} />
                    </Grid>
                </Grid>
            );
        } else
            return (
                <Grid container spacing={1}>
                    <Grid item xs={2}></Grid>
                    <Grid item xs={10}>
                        <EventCard event={event} />
                    </Grid>
                </Grid>
            );
    });
}

export default Timeline;
