import React from "react";
import Container from "@material-ui/core/container";
import Timeline from "../comps/timeline";
import fetch from "isomorphic-unfetch";
import { User, Calendar, Event } from "../classes";

function startOfDay(date) {
    if (!date instanceof Date) {
        console.error("傳入的時間不是合法的 Date 物件。");
        return null;
    }
    date = new Date(date);
    var time = new Date();
    time.setTime(date.getTime());
    time.setHours(0, 0, 0);
    return time;
}

function endOfDay(date) {
    if (!date instanceof Date) {
        console.error("傳入的時間不是合法的 Date 物件。");
        return null;
    }
    date = new Date(date);
    var time = new Date();
    time.setTime(date.getTime());
    time.setHours(23, 59, 59);
    return time;
}

function fillEvents(events) {
    var filled = new Array();
    var time = startOfDay(events[0].startTime);
    events.map(event => {
        var startTime = new Date(event.startTime);
        var endTime = new Date(event.endTime);
        filled.push(new Event({ startTime: time, endTime: startTime }, true));
        filled.push(event);
        time.setTime(endTime.getTime());
    });
    filled.push(new Event({ startTime: time, endTime: endOfDay(events[0].startTime) }, true));
    return filled;
}

class index extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userdata: {},
            filled: []
        };
    }

    static async getInitialProps() {
        const res = await fetch("http://localhost:3000/api/getuserdata");
        const json = await res.json();
        var userdata = new User(json);
        var filled = fillEvents(userdata.calendars[0].events);
        return { userdata: userdata, filled: filled };
    }

    render() {
        const timeline = {
            overflowY: "scroll",
            maxHeight: "90vh",
            padding: 24
        };
        console.log(this.state.filled);
        return (
            <Container maxWidth="sm">
                <div style={timeline}>
                    <Timeline events={this.state.filled} />
                </div>

                <style global jsx>{`
                    body {
                        background: #333333;
                    }
                    ::-webkit-scrollbar {
                        width: 5px;
                    }
                    ::-webkit-scrollbar-track {
                        -webkit-border-radius: 10px;
                        border-radius: 10px;
                        margin: 80px 0 5px 0;
                    }
                    ::-webkit-scrollbar-thumb {
                        -webkit-border-radius: 4px;
                        border-radius: 4px;
                        background: rgb(80, 80, 80);
                    }
                `}</style>
            </Container>
        );
    }
}

export default index;
