import React from "react";
import Paper from "@material-ui/core/Paper";
import { Event } from "../classes";
import Tooltip from "@material-ui/core/Tooltip";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";

const HtmlTooltip = withStyles(theme => ({
    tooltip: {
        backgroundColor: "#f5f5f9",
        color: "rgba(0, 0, 0, 0.87)",
        maxWidth: 220,
        border: "1px solid #dadde9"
    }
}))(Tooltip);

class EventCard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            elevation: 0
        };
        this.handleMouseOver = this.handleMouseOver.bind(this);
        this.handleMouseLeave = this.handleMouseLeave.bind(this);
    }

    handleMouseOver() {
        this.setState({
            elevation: 20
        });
    }

    handleMouseLeave() {
        this.setState({
            elevation: 0
        });
    }

    render() {
        if (this.props.event instanceof Event) {
            if (this.props.event.isEmpty) {
                return EmptySections(this.props.event.startTime, this.props.event.endTime).map(emptyCard => {
                    return emptyCard;
                });
            } else {
                const style = {
                    height: this.props.height != undefined ? this.props.height : this.props.event.duration,
                    backgroundImage: "linear-gradient(315deg, " + this.props.event.color[0] + " 0%, " + this.props.event.color[1] + " 100%)",
                    fontSize: 8,
                    paddingLeft: 16,
                    marginTop: this.props.height != undefined ? 15 : 0,
                    paddingTop: 6,
                    paddingBottom: 6
                };

                /** compose event info of card */
                var lineAmount =
                    this.props.height != undefined
                        ? parseInt(this.props.height / 20) > 1
                            ? parseInt(this.props.height / 20) - 1
                            : 1
                        : parseInt(this.props.event.duration / 20) > 1
                        ? parseInt(this.props.event.duration / 20) - 1
                        : 1;
                var eventInfo = [];
                eventInfo.push(
                    this.props.event.isAllDayEvent() ? (
                        <p key="title" style={{ color: "white" }}>
                            {this.props.event.title}{" "}
                        </p>
                    ) : (
                        <p key="title" style={{ color: "white" }}>
                            {this.props.event.title}{" "}
                            <strong style={{ marginLeft: 16, color: "rgba(255,255,255,0.4)" }}>{this.props.event.getDurationString()}</strong>
                        </p>
                    )
                );
                if (!this.props.event.isAllDayEvent())
                    eventInfo.push(
                        <p style={{ color: "rgba(255,255,255,0.8)" }} key="duration">
                            {this.props.event.duration} 分鐘
                        </p>
                    );
                else {
                    eventInfo.push(
                        <p style={{ color: "rgba(255,255,255,0.8)" }} key="duration">
                            全天事件
                        </p>
                    );
                }
                eventInfo.push(
                    <p style={{ color: "rgba(255,255,255,0.8)" }} key="cal">
                        Event ID : {this.props.event.id}
                    </p>
                );
                return (
                    <HtmlTooltip
                        placement="right-start"
                        disableFocusListener
                        disableHoverListener
                        disableTouchListener
                        title={
                            <React.Fragment>
                                <Typography color="inherit">Tooltip with HTML</Typography>
                                <em>{"And here's"}</em> <b>{"some"}</b> <u>{"amazing content"}</u>. {"It's very engaging. Right?"}
                            </React.Fragment>
                        }
                    >
                        <Paper
                            style={style}
                            elevation={this.state.elevation}
                            onMouseEnter={this.handleMouseOver}
                            onMouseLeave={this.handleMouseLeave}
                            onClick={this.handleClick}
                            key={this.props.event.id}
                        >
                            {eventInfo.slice(0, lineAmount).map(info => {
                                return info;
                            })}
                        </Paper>
                    </HtmlTooltip>
                );
            }
        } else {
            console.error("渲染事件卡片時接收到了不符合規範的 Event 物件。");
            return null;
        }
    }
}

function emptyCard(startTime, endTime) {
    endTime = endTime;
    startTime = startTime;
    const duration = (endTime - startTime) / 60000;
    var cardStyle = {
        height: duration,
        backgroundColor: "transparent",
        borderBottomStyle: endTime.getMinutes() == 0 ? "solid" : "none",
        borderBottomColor: "#444444",
        borderBottomWidth: 1,
        borderTopStyle: startTime.getMinutes() == 0 ? "solid" : "none",
        borderTopColor: "#444444",
        borderTopWidth: 1
    };
    return (
        <div style={cardStyle} key={startTime.getTime()}>
            {/** startTime.getHours() +
                ":" +
                startTime.getMinutes() +
                " - " +
                endTime.getHours() +
                ":" +
             endTime.getMinutes() */}
        </div>
    );
}

function EmptySections(start, end) {
    if (end.getHours() < start.getHours()) {
        end.setHours(23, 59, 59);
    }
    var arr = new Array(end.getHours() - start.getHours() + 1);
    if (start.getHours() == end.getHours()) {
        arr[0] = emptyCard(start, end);
    } else if (start.getHours() + 1 == end.getHours()) {
        var a = new Date(end);
        a.setMinutes(0);
        arr[0] = emptyCard(start, a);
        arr[1] = emptyCard(a, end);
    } else {
        var a = new Date(start);
        a.setHours(start.getHours() + 1, 0);
        arr[0] = emptyCard(start, a);
        for (var i = start.getHours() + 1, j = 1; i < end.getHours(); i++, j++) {
            var a = new Date(start);
            a.setHours(i, 0);
            var b = new Date(start);
            b.setHours(i + 1, 0);
            arr[j] = emptyCard(a, b);
        }
        var a = new Date(end);
        a.setMinutes(0);
        arr[end.getHours() - start.getHours()] = emptyCard(a, end);
    }
    return arr;
}

export default EventCard;
