import React from "react";
import Container from "@material-ui/core/container";
import Timeline from "../comps/timeline";
import fetch from "isomorphic-unfetch";

/** Parse dates of json file into Date object */
function parseDate(jsonUserData) {
    return new Promise(resolve => {
        jsonUserData.calendars.map(calendar => {
            calendar.events.map(event => {
                event.startTime = new Date(event.startTime);
                event.endTime = new Date(event.endTime);
            });
        });
        resolve(jsonUserData);
    });
}

class index extends React.Component {
    static async getInitialProps() {
        const res = await fetch("http://localhost:3000/api/getuserdata");
        const json = await res.json();
        const userdata = await parseDate(json);
        return { userdata: userdata };
    }

    render() {
        const timeline = {
            overflowY: "scroll",
            maxHeight: "90vh",
            padding: 24
        };
        console.log(JSON.stringify(this.props));
        return (
            <Container maxWidth="sm">
                <div style={timeline}>
                    <Timeline events={this.props.userdata.calendars[0].events} />
                </div>

                <style global jsx>{`
                    body {
                        background: #333333;
                    }
                    ::-webkit-scrollbar {
                        width: 5px;
                    }
                    ::-webkit-scrollbar-track {
                        -webkit-border-radius: 10px;
                        border-radius: 10px;
                        margin: 80px 0 5px 0;
                    }
                    ::-webkit-scrollbar-thumb {
                        -webkit-border-radius: 4px;
                        border-radius: 4px;
                        background: rgb(80, 80, 80);
                    }
                `}</style>
            </Container>
        );
    }
}

export default index;
